class ImagesController < ApplicationController
  def index
    # we shall add more conversions later
    @conversion = params[:conversion] || "text"
  end

  def create
    result = ImageOrchestratorService.perform(image_params[:conversion], image_params[:images])
    if result.success?
      respond_to do |format|
        format.turbo_stream do
          render turbo_stream: turbo_stream.replace("results", partial: "results_#{image_params[:conversion]}", locals: { pages:  result.data }) +
          turbo_stream.replace("image_form", partial: "image_form", locals: { conversion: image_params[:conversion] })
        end
        format.html { redirect_to root_path }
      end
    else
      flash[:error] = result.error
      turbo_stream.replace "flash", partial: "flash"
      head :unprocessable_entity
    end
  end

  private

  def image_params
    params.permit(:conversion, images: [])
  end

  def html_text
  [ "<p><img src=\"data:image/jpeg;base64,\" alt=\"OCR Image\" /></p>\n\n<p><img src=\"data:image/jpeg;base64,\" alt=\"OCR Image\" /></p>\n\n<h2 id=\"for-your-security\">For your security</h2>\n\n<p>Please enter the last 3 digits of the number in the signature panel on the back of your card.</p>\n\n<p><img src=\"data:image/jpeg;base64,\" alt=\"OCR Image\" /></p>\n\n<table>\n  <tbody>\n    <tr>\n      <td>Contact us</td>\n      <td>FAQ</td>\n      <td>Privacy</td>\n    </tr>\n  </tbody>\n</table>\n\n<p>Have a coupon</p>\n" ]
  end
end
